    public static String[] getStations(){
        String[] names = new String[stations.length];
        for(int i = 0; i < stations.length; i++){
            names[i] = stations[i][0];
        }
        return names;
    }

    private static boolean stationExists(String station){
        return Arrays.asList(getStations()).indexOf(station) > -1;
    }

    public static String codeFromStation(String station){
        for(String[] pair : stations){
            if(pair[0].equals(station)){
                return pair[1];
            }
        }
        return null;
    }

    public static String stationFromCode(String code){
        for(String[] pair : stations){
            if(pair[1].equals(code)){
                return pair[0];
            }
        }
        return null;
    }

    public static String getFormattedTime(String time){
        Date date;
        try{
            date = new SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss").parse(time);
        } catch(ParseException e){
            e.printStackTrace();
            return null;
        }

        Calendar cal = Calendar.getInstance();
        cal.setTime(date);

        return new SimpleDateFormat("HH:mm").format(cal.getTime());
    }

    public static Query buildQuery(String from, String to){
        Query query = new Query();

        query.setAdults(1);
        query.setChildren(0);
        query.setDestination(Utils.codeFromStation(to));
        query.setJourneyType("Single");
        query.setOrigin(Utils.codeFromStation(from));

        OutboundJourney outbound = new OutboundJourney();
        outbound.setTime(Utils.getCurrentTime());
        outbound.setType("LeaveAfter");
        query.setOutboundJourney(outbound);

        Railcard railcard = new Railcard();
        railcard.setCode("YNG");
        railcard.setCount(1);
        query.setRailcards(Arrays.asList(railcard));

        query.setShowCancelledTrains(true);

        return query;
    }

    private static String getCurrentTime(){
        Calendar cal = Calendar.getInstance();
        cal.setTime(new Date());
        cal.set(Calendar.SECOND, 0);

        return new SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss").format(cal.getTime());
    }

    public static void saveJourney(Journey journey, Context context){
        SharedPreferences prefs = context.getSharedPreferences("prefs", MODE_PRIVATE);

        /* generate a random id to associate with the journey for alarms and notifications */
        journey.setId((int)(Math.random() * 10000));

        /* get our currently saved journeys */
        Journey[] oldJourneys = new Gson().fromJson(prefs.getString("journeys", "[]"), Journey[].class);

        /* copy our saved journeys to a new array with length + 1 */
        Journey[] newJourneys = Arrays.copyOf(oldJourneys, oldJourneys.length + 1);

        /* save our new journey to the end of the array */
        newJourneys[newJourneys.length - 1] = journey;

        /* update our stored journeys */
        prefs.edit().putString("journeys", new Gson().toJson(newJourneys)).commit();

        NotificationReceiver.setupPolling(journey, context);
    }

    public static Journey[] getAllJourneys(Context context){
        SharedPreferences prefs = context.getSharedPreferences("prefs", MODE_PRIVATE);
        return new Gson().fromJson(prefs.getString("journeys", "[]"), Journey[].class);
    }

    public static void removeJourney(int index, Context context) {
        /* need the new ArrayList() wrapper around Arrays.asList because .asList
           returns an unmodifiable list and we need to be able to remove an element */
        List<Journey> journeys = new ArrayList(Arrays.asList(getAllJourneys(context)));

        NotificationReceiver.removePolling(journeys.get(index), context);

        SharedPreferences prefs = context.getSharedPreferences("prefs", MODE_PRIVATE);

        journeys.remove(index);

        /* convert our journey list to an array and save it */
        prefs.edit().putString("journeys", new Gson().toJson(journeys.toArray())).commit();
    }

