    public static String getTimeDifference(String time1, String time2){
        try {
            SimpleDateFormat format = new SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss");

            if(time1 == null || time2 == null) return "";

            /* get delay in milliseconds then convert to minutes */
            long delay = format.parse(time1).getTime() - format.parse(time2).getTime();
            delay /= 1000 * 60; /* millisecond to minutes */

            int hours = (int) Math.floor(delay / 60);
            int minutes = (int)(delay % 60);

            String strHours = String.valueOf(hours);
            String strMinutes = String.valueOf(minutes);

            String delayText = "";
            if(hours > 0){
                delayText += strHours + (hours > 1 ? " hours " : " hour ");
            }

            if(minutes > 0){
                delayText += strMinutes + (minutes > 1 ? " minutes" : " minute");
            }

            return delayText;
        } catch(ParseException e){
            return "";
        }
    }

    public static String getDuration(String departure, String arrival){
        try {
            SimpleDateFormat format = new SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss");

            long timeDifference = format.parse(arrival).getTime() - format.parse(departure).getTime();
            timeDifference /= 1000 * 60; /* milliseconds to minutes */

            int hours = (int) Math.floor(timeDifference / 60);
            int minutes = (int)(timeDifference % 60);

            return (hours > 0 ? String.valueOf(hours) + "h " : "") + String.valueOf(minutes) + "m";
        } catch(ParseException e){
            return "";
        }
    }

    public static String getBestDepartTime(Origin origin){
        /* if there is real time data present, the train is probably delayed */
        if(origin.getRealTime() == null) {
            return origin.getScheduledTime();
        } else {
            return origin.getRealTime();
        }
    }

    public static String getBestArriveTime(Destination destination){
        /* if there is real time data present, the train is probably delayed */
        if(destination.getRealTime() == null) {
            return destination.getScheduledTime();
        } else {
            return destination.getRealTime();
        }
    }

    public static boolean areJourneysIdentical(Journey j1, Journey j2){
        if(!j1.getDestination().equals(j2.getDestination())) return false;
        if(!j1.getOrigin().equals(j2.getOrigin())) return false;
        if(j1.getLegs().size() !=  j2.getLegs().size()) return false;
        if(!j1.getDirection().equals(j2.getDirection())) return false;
        if(!j1.getLegs().get(0).getOrigin().getScheduledTime().equals(j2.getLegs().get(0).getOrigin().getScheduledTime())) return false;
        if(!j1.getLegs().get(0).getDestination().getScheduledTime().equals(j2.getLegs().get(0).getDestination().getScheduledTime())) return false;

        /* sometimes the platform may be null, so let's check if there is any platform given */
        for(int i = 0; i < j1.getLegs().size(); i++){
            String originPlatform1 = j1.getLegs().get(i).getOrigin().getPlatform();
            String originPlatform2 = j2.getLegs().get(i).getOrigin().getPlatform();
            if(originPlatform1 != null && originPlatform2 != null){
                if(!originPlatform1.equals(originPlatform2)) return false;
            }

            String destinationPlatform1 = j1.getLegs().get(i).getDestination().getPlatform();
            String destinationPlatform2 = j2.getLegs().get(i).getDestination().getPlatform();
            if(destinationPlatform1 != null && destinationPlatform2 != null){
                if(!destinationPlatform1.equals(destinationPlatform2)) return false;
            }
        }

        return true;
    }

