    public static int getScreenHeight(Context context){
        WindowManager wm = (WindowManager) context.getSystemService(Context.WINDOW_SERVICE);
        Display display = wm.getDefaultDisplay();
        Point size = new Point();
        display.getSize(size);

        return size.y;
    }

    public static void saveRecentSearch(String from, String to, Context context){
        String[][] searches = getRecentSearches(context);

        /* if our most recent search equals the one being saved, don't save it */
        if(searches[0][0] != null && searches[0][1] != null && searches[0][0].equals(from) && searches[0][1].equals(to)) return;

        /* shift search history right */
        for(int i = searches.length - 1; i > 0; i--){
            searches[i] = searches[i - 1];
        }

        /* save our search as the newest search */
        searches[0] = new String[] { from, to };

        SharedPreferences prefs = context.getSharedPreferences("prefs", MODE_PRIVATE);
        prefs.edit().putString("recent searches", new Gson().toJson(searches)).commit();
    }

    public static String[][] getRecentSearches(Context context){
        SharedPreferences prefs = context.getSharedPreferences("prefs", MODE_PRIVATE);

        /* if we have no searches saved, return an empty list */
        if(isRecentSearchesEmpty(context)){
            return new String[5][2];
        }

        return new Gson().fromJson(prefs.getString("recent searches", ""), String[][].class);
    }

    public static boolean isRecentSearchesEmpty(Context context){
        SharedPreferences prefs = context.getSharedPreferences("prefs", MODE_PRIVATE);
        return prefs.getString("recent searches", "default").equals("default");
    }

    public static String formSearchError(String from, String to){
        if(from == null || from.equals("")){
            return "An origin station is required.";
        }

        if(to == null || to.equals("")){
            return "A destination station is required.";
        }

        if(!Utils.stationExists(from)){
            return from + " is not a station.";
        }

        if(!Utils.stationExists(to)){
            return to + " is not a station.";
        }

        return "";
    }

    public static String capitalise(String serviceProvider){
        if(serviceProvider.isEmpty()) return "";

        String newString = "";
        String[] words = serviceProvider.toLowerCase().split(" ");

        for(int i = 0; i < words.length; i++){
            newString += Character.toUpperCase(words[i].charAt(0));
            newString += words[i].substring(1);
            if(i != words.length - 1) newString += " ";
        }
        return newString;
    }

